import {
  Component,
  Inject,
  NgZone,
  OnDestroy,
  OnInit,
  Renderer2,
  ElementRef
} from "@angular/core";


@Component({
  selector: "app-mainpage",
  templateUrl: "mainpage.component.html",
  styleUrls: ["./mainpage.component.scss"],
})
export class MainPageComponent implements OnInit, OnDestroy {
  imageObject: Array<any> = [{
    image: '../../../assets/images/20231017_153241.jpg',
    thumbImage: '../../../assets/images/20231017_153241.jpg',
    alt: 'alt of image',
  },
    {
    image: '../../../assets/images/20240113_212045.jpg',
    thumbImage: '../../../assets/images/20240113_212045.jpg',
    alt: 'alt of image',
  },
    {
    image: '../../../assets/images/20231125_164919.jpg',
    thumbImage: '../../../assets/images/20231125_164919.jpg',
    alt: 'Image alt',
    order: 1 //Optional: if you pass this key then slider images will be arrange according @input: slideOrderType
  },
    {
      image: '../../../assets/images/20231114_170603.jpg',
      thumbImage: '../../../assets/images/20231114_170603.jpg',
      alt: 'alt of image',
    },
    {
      image: '../../../assets/images/20231120_223550.jpg',
      thumbImage: '../../../assets/images/20231120_223550.jpg',
      alt: 'alt of image',
    },
  ];
  public isActiveBtn = false;
  constructor() {
  }
  ngOnInit() {
}
  clickButton() {
    this.isActiveBtn = !this.isActiveBtn;
  }
ngOnDestroy() {
}
}
<div class="container">
<!--  <div class="secret_btn">-->
<!--    <p> ты что-то хочешь?</p>-->
<!--  </div>-->
  <div class="wrapper">
    <div class="button secret_btn" (click)="clickButton()" [class.active] = "isActiveBtn == true">
      <span class="button-text">ты что-то хочешь</span>
      <div class="button-backgrounds">
        <div class="button-circle button-circle1"></div>
        <div class="button-circle button-circle2"></div>
        <div class="button-circle button-circle3"></div>
        <div class="button-circle button-circle4"></div>
      </div>
    </div>
  </div>

  <div class="wrapper">
    <div class="popup">
      <div class="popup-inside">
        <div class="backgrounds">
          <div class="background"></div>
          <div class="background background2"></div>
          <div class="background background3"></div>
          <div class="background background4"></div>
          <div class="background background5"></div>
          <div class="background background6"></div>
        </div>
      </div>
      <div class="content">
        <div class="content-wrapper">
          <h1>well done</h1>
          <p>Do you like it?</p>
          <p class="try-again" onclick="document.body.classList.remove('active')">Yes! I will try again</p>
        </div>
      </div>
    </div>
  </div>

  <div class="slider">
    <ng-image-slider
      [images]="imageObject" #nav
    [imageSize]="{width: '200px', height: '250px', space: 10}"
    [infinite]="true"
    [animationSpeed]="0.1">

    </ng-image-slider>
  </div>


</div>
